&lt;h2 id=&quot;primes-moderate-hard&quot;&gt;primes(moderate)&#x2F;(hard)&lt;&#x2F;h2&gt;
&lt;pre data-lang=&quot;C&quot; class=&quot;language-C &quot;&gt;&lt;code class=&quot;language-C&quot; data-lang=&quot;C&quot;&gt;#include &amp;quot;kernel&amp;#x2F;types.h&amp;quot;
#include &amp;quot;kernel&amp;#x2F;stat.h&amp;quot;
#include &amp;quot;user&amp;#x2F;user.h&amp;quot;

#define READ 0
#define WRITE 1

__attribute__((noreturn)) &amp;#x2F;&amp;#x2F; æ²¡æŠ¥é€’å½’è­¦å‘Šçš„è¯å°±ä¸ç”¨åŠ 
void sieve_algo (int left[2], int depth)
{
    close(left[WRITE]);

    int prime, temp, pid, right[2];

    &amp;#x2F;&amp;#x2F; è¯»å–ç¬¬ä¸€ä¸ªæ•°å­—
    if (read(left[READ], &amp;amp;prime, sizeof(int)) == 0)
    {
        close(left[READ]);
        exit(0);
    }

    if (depth &amp;gt; 15)
    {
        fprintf(2, &amp;quot;error: stackoverflow\n&amp;quot;);
        close(right[READ]);
        close(right[WRITE]);
        close(left[READ]);
        exit(1);
    }

    printf(&amp;quot;prime: %d\n&amp;quot;, prime);
    pipe(right);

    if ((pid = fork()) &amp;gt; 0)
    {
        close(right[READ]);

        &amp;#x2F;&amp;#x2F; ç­›é€‰
        while(read(left[READ], &amp;amp;temp, sizeof(int)))
        {
            if (temp % prime != 0)
            {
                if (write(right[WRITE], &amp;amp;temp, sizeof(int)) != sizeof(int))
                {
                    fprintf(2, &amp;quot;write error\n&amp;quot;);
                }
            }
        }
        close(right[WRITE]);
        wait(0);
        exit(0);
    }
    else if (pid == 0)
    {
        close(left[READ]);
        close(right[WRITE]);
        sieve_algo(right, depth + 1);
        exit(0);
    }
    else
    {
        fprintf(2, &amp;quot;fork error...\n&amp;quot;);
        close(right[READ]);
        close(right[WRITE]);
        close(left[READ]);
        exit(1);
    }
}

int main(int argc, char* argv[])
{
    int pid, p[2];
    pipe(p); 

    if ((pid = fork()) &amp;gt; 0) 
    {
        close(p[READ]);

        &amp;#x2F;&amp;#x2F; æŠŠ2åˆ°35ä¸€ä¸ªä¸€ä¸ªå†™è¿›å»Pç®¡é“
        for (int i = 2; i &amp;lt;= 35; i++)
        {
            write(p[WRITE], &amp;amp;i, sizeof(int));
        }

        &amp;#x2F;&amp;#x2F; Pçš„å·¥ä½œæ­£å¼ç»“æŸ
        close(p[WRITE]); 
        wait(0); &amp;#x2F;&amp;#x2F; ç­‰å¾…å­è¿›ç¨‹ç»“æŸ
        exit(0); &amp;#x2F;&amp;#x2F; é€€å‡ºç¨‹åº
    }
    else if (pid == 0)  &amp;#x2F;&amp;#x2F; å­è¿›ç¨‹
    {
        sieve_algo(p, 1); 
        exit(0);
    }
    else
    {
        fprintf(2, &amp;quot;fork error\n&amp;quot;);
        exit(1);
    }
}
