char *
fmtname (char *path)
{
  char *p;

  for (p = path + strlen (path); p &amp;gt;= path &amp;amp;&amp;amp; *p != &amp;#x27;&amp;#x2F;&amp;#x27;; --p)
    ;
  return p + 1;
}

void
find (char *path, char *filename)
{
  char buf[512], *p;
  int fd;
  struct stat st;
  struct dirent de;

  if ((fd = open (path, 0)) &amp;lt; 0)
  {
    fprintf (2, &amp;quot;find: cannot open %s\n&amp;quot;, path);
    return;
  }

  if (fstat (fd, &amp;amp;st) &amp;lt; 0)
  {
    fprintf (2, &amp;quot;find: cannot stat %s\n&amp;quot;, path);
    close (fd);
    return;
  }

  switch (st.type)
  {
    case T_FILE:
      if (strcmp (fmtname (path), filename) == 0)
      {
        printf (&amp;quot;%s\n&amp;quot;, path);
      }
      break;

    case T_DIR:
      strcpy (buf, path);
      p = buf + strlen (buf);
      *p++ = &amp;#x27;&amp;#x2F;&amp;#x27;;

      while (read (fd, &amp;amp;de, sizeof (de)) == sizeof (de))
      {
        if (de.inum == 0 || strcmp (de.name, &amp;quot;.&amp;quot;) == 0
          || strcmp (de.name, &amp;quot;..&amp;quot;) == 0)
          continue;

        memmove (p, de.name, DIRSIZ);
        p[DIRSIZ] = 0;
        find (buf, filename);
      }
      break;
    default:
      break;
  }
  close (fd);
}

int
main (int argc, char *argv[])
{
  if (argc &amp;lt; 3)
  {
    fprintf (2, &amp;quot;usage: find &amp;lt;start_path&amp;gt; &amp;lt;file_name&amp;gt;\n&amp;quot;);
    exit (0);
  }
  find (argv[1], argv[2]);
  exit (0);
}
